	(1797,316,'repeatable_fields_2','a:5:{i:0;a:5:{s:13:\"texto_espanol\";s:102:\"Jun Lajun Talaan es la carga de Sak Ixik, imagen de palo, imagen de pedernal. Es decir lucha o guerra.\";s:6:\"img_hp\";s:8:\"46_1.png\";s:10:\"texto_maya\";s:62:\"<i>Jun Lajun Talaan ukuch Sak Ixik, te\' baah, tok\' [baah]</i>.\";s:12:\"coordenada_x\";s:2:\"73\";s:12:\"coordenada_y\";s:2:\"20\";}i:1;a:5:{s:13:\"texto_espanol\";s:69:\"la lumbre de Sak Ixik es la carga, [el augurio es] castigo por fuego.\";s:6:\"img_hp\";s:8:\"46_2.png\";s:10:\"texto_maya\";s:45:\"<i>k\'ahk\' Sak Ixik ukuch k\'ahk\' te\' tuun</i>.\";s:12:\"coordenada_x\";s:3:\"198\";s:12:\"coordenada_y\";s:2:\"20\";}i:2;a:5:{s:13:\"texto_espanol\";s:58:\"Kiimil es la carga de Sak Ixik, [el augurio es] mortandad.\";s:6:\"img_hp\";s:8:\"46_3.png\";s:10:\"texto_maya\";s:39:\"<i>Kiimil ukuch Sak Ixik, cham[a]l</i>.\";s:12:\"coordenada_x\";s:3:\"349\";s:12:\"coordenada_y\";s:1:\"1\";}i:3;a:5:{s:13:\"texto_espanol\";s:62:\"Ahan(?) es la carga de Sak Ixik, [el augurio] es alimento (?).\";s:6:\"img_hp\";s:8:\"46_4.png\";s:10:\"texto_maya\";s:37:\"<i>Ahan(?) ukuch Sak Ixik, o\'och</i>.\";s:12:\"coordenada_x\";s:3:\"478\";s:12:\"coordenada_y\";s:1:\"1\";}i:4;a:5:{s:13:\"texto_espanol\";s:74:\"la pepita(?) blanca es la carga de Sak Ixik, [el augurio es] mucha comida.\";s:6:\"img_hp\";s:8:\"46_5.png\";s:10:\"texto_maya\";s:46:\"<i>sak saak(?) ukuch Sak Ixik, ox wi\'[il]</i>.\";s:12:\"coordenada_x\";s:3:\"626\";s:12:\"coordenada_y\";s:1:\"1\";}}'),
