	(3381,396,'repeatable_fields_2','a:5:{i:0;a:4:{s:13:\"texto_espanol\";s:20:\"Augurio muy dañado.\";s:6:\"img_hp\";s:8:\"53_1.png\";s:12:\"coordenada_x\";s:3:\"262\";s:12:\"coordenada_y\";s:2:\"14\";}i:1;a:5:{s:13:\"texto_espanol\";s:54:\"el niño Kiimil descendió, [el augurio es] mortandad.\";s:6:\"img_hp\";s:8:\"53_2.png\";s:10:\"texto_maya\";s:32:\"emi Kiimil] ch\'ok, cham[a]l</i>.\";s:12:\"coordenada_x\";s:3:\"393\";s:12:\"coordenada_y\";s:2:\"14\";}i:2;a:5:{s:13:\"texto_espanol\";s:37:\"Ik\'(?), el niño sagrado, descendió.\";s:6:\"img_hp\";s:8:\"53_3.png\";s:10:\"texto_maya\";s:35:\"<i>[emi] k\'uh[ul] ch\'ok Ik\'(?)</i>.\";s:12:\"coordenada_x\";s:3:\"462\";s:12:\"coordenada_y\";s:2:\"14\";}i:3;a:5:{s:13:\"texto_espanol\";s:62:\"el niño Jun Lajun Talaan descendió, [el augurio es] ruin(?).\";s:6:\"img_hp\";s:8:\"53_4.png\";s:10:\"texto_maya\";s:45:\"<i>[emi Jun] Lajun Talaan ch\'ok, k\'as(?)</i>.\";s:12:\"coordenada_x\";s:3:\"527\";s:12:\"coordenada_y\";s:2:\"14\";}i:4;a:5:{s:13:\"texto_espanol\";s:31:\"el dios niño Chaak descendió.\";s:6:\"img_hp\";s:8:\"53_5.png\";s:10:\"texto_maya\";s:30:\"<i>[emi] Chaak ch\'ok k\'uh</i>.\";s:12:\"coordenada_x\";s:3:\"598\";s:12:\"coordenada_y\";s:2:\"14\";}}'),
