	(3395,397,'repeatable_fields_2','a:6:{i:0;a:5:{s:13:\"texto_espanol\";s:74:\"Ho\' Tuun Itzam se estrenó, pescado es el alimento de Ixik Uh, pepitas(?).\";s:6:\"img_hp\";s:8:\"54_1.png\";s:10:\"texto_maya\";s:59:\"<i>joyaj Ho\' Tuun Itzam, kay upa\' Ixik Uh, saak[i]l(?)</i>.\";s:12:\"coordenada_x\";s:3:\"218\";s:12:\"coordenada_y\";s:1:\"1\";}i:1;a:5:{s:13:\"texto_espanol\";s:51:\"Yaax Bolon se estrenó, pedernales son su alimento.\";s:6:\"img_hp\";s:8:\"54_2.png\";s:10:\"texto_maya\";s:37:\"<i>joy[aj] Yaax Bolon, tok\' upa\'</i>.\";s:12:\"coordenada_x\";s:3:\"337\";s:12:\"coordenada_y\";s:1:\"1\";}i:2;a:5:{s:13:\"texto_espanol\";s:41:\"K\'awiil se estrenó, pavo es su alimento.\";s:6:\"img_hp\";s:8:\"54_3.png\";s:10:\"texto_maya\";s:34:\"<i>joy[aj] K\'awiil, ulum upa\'</i>.\";s:12:\"coordenada_x\";s:3:\"388\";s:12:\"coordenada_y\";s:1:\"1\";}i:3;a:5:{s:13:\"texto_espanol\";s:49:\"Kiimil se estrenó, mortandad, [augurio] ruin(?).\";s:6:\"img_hp\";s:8:\"54_4.png\";s:10:\"texto_maya\";s:41:\"<i>joy[aj] Kiimil, cham[a]l, k\'as(?)</i>.\";s:12:\"coordenada_x\";s:3:\"439\";s:12:\"coordenada_y\";s:1:\"1\";}i:4;a:5:{s:13:\"texto_espanol\";s:53:\"Ahan(?) se estrenó, pierna de venado es su alimento.\";s:6:\"img_hp\";s:8:\"54_5.png\";s:10:\"texto_maya\";s:39:\"<i>joy[aj] Ahan(?), yook chij upa\'</i>.\";s:12:\"coordenada_x\";s:3:\"495\";s:12:\"coordenada_y\";s:1:\"1\";}i:5;a:5:{s:13:\"texto_espanol\";s:55:\"el zopilote, refacción(?) es su alimento, se estrenó.\";s:6:\"img_hp\";s:8:\"54_6.png\";s:10:\"texto_maya\";s:37:\"<i>joy[aj] o\'och(?), upa\' k\'uuch</i>.\";s:12:\"coordenada_x\";s:3:\"551\";s:12:\"coordenada_y\";s:1:\"1\";}}'),
