	(6139,633,'repeatable_fields_2','a:4:{i:0;a:5:{s:13:\"texto_espanol\";s:90:\"el Varón Rojo se puso de pie en el oriente, calabazas sin pelo, tamales de venado, Chaak.\";s:6:\"img_hp\";s:9:\"r17_1.png\";s:10:\"texto_maya\";s:77:\"<i>wa’laj lak’in Chak Xib bil ka[’], chij waaj, Chaak, waaj upa’</i>.\";s:12:\"coordenada_x\";s:3:\"147\";s:12:\"coordenada_y\";s:1:\"1\";}i:1;a:5:{s:13:\"texto_espanol\";s:111:\"Chaak Varón Blanco se puso de pie en el norte, tamales de pavo, grandes productos comestibles son su alimento.\";s:6:\"img_hp\";s:9:\"r17_2.png\";s:10:\"texto_maya\";s:65:\"<i>wa’laj nal Sak Xib Chaak, kutz waaj, chak tzi[h] upa’</i>.\";s:12:\"coordenada_x\";s:3:\"280\";s:12:\"coordenada_y\";s:1:\"1\";}i:2;a:5:{s:13:\"texto_espanol\";s:108:\"Chaak Varón Negro se puso de pie en el oeste, tamales de iguana, tamales, miel robada negra es su alimento.\";s:6:\"img_hp\";s:9:\"r17_3.png\";s:10:\"texto_maya\";s:84:\"<i>wa’laj chik’in Ik’ Xib Chaak, huh waaj, waaj, ik’ pa[’] kab upa’</i>.\";s:12:\"coordenada_x\";s:3:\"388\";s:12:\"coordenada_y\";s:1:\"1\";}i:3;a:5:{s:13:\"texto_espanol\";s:47:\"Chaak Varón Amarillo se puso de pie en el sur.\";s:6:\"img_hp\";s:9:\"r17_4.png\";s:10:\"texto_maya\";s:79:\"<i>wa’laj nohol K’an Xib Chaak, kay, ki[’] waaj, ya’ax waaj upa’</i>.\";s:12:\"coordenada_x\";s:3:\"542\";s:12:\"coordenada_y\";s:1:\"1\";}}'),
