	(6153,639,'repeatable_fields_2','a:9:{i:0;a:5:{s:13:\"texto_espanol\";s:50:\"Chaak lo creó [en el] árbol rojo, en el oriente.\";s:6:\"img_hp\";s:9:\"r18_1.png\";s:10:\"texto_maya\";s:49:\"<i>uch’ab[a]w Chaak chak te’ ti lak’in</i>.\";s:12:\"coordenada_x\";s:1:\"4\";s:12:\"coordenada_y\";s:2:\"70\";}i:1;a:5:{s:13:\"texto_espanol\";s:40:\"Chaak está en el año blanco del norte.\";s:6:\"img_hp\";s:9:\"r18_2.png\";s:10:\"texto_maya\";s:31:\"<i>ayan Chaak sak haab nal</i>.\";s:12:\"coordenada_x\";s:3:\"101\";s:12:\"coordenada_y\";s:2:\"70\";}i:2;a:5:{s:13:\"texto_espanol\";s:44:\"Chaak lo creó en el árbol negro del oeste.\";s:6:\"img_hp\";s:9:\"r18_3.png\";s:10:\"texto_maya\";s:49:\"<i>uch’ahb[a]w Chaak ik’ te’ chik’in</i>.\";s:12:\"coordenada_x\";s:3:\"182\";s:12:\"coordenada_y\";s:2:\"70\";}i:3;a:5:{s:13:\"texto_espanol\";s:42:\"Chaak está en el árbol amarillo del sur.\";s:6:\"img_hp\";s:9:\"r18_4.png\";s:10:\"texto_maya\";s:40:\"<i>ayan[a]w lak’in Chak Xib Chaak</i>.\";s:12:\"coordenada_x\";s:3:\"261\";s:12:\"coordenada_y\";s:2:\"70\";}i:4;a:5:{s:13:\"texto_espanol\";s:49:\"Chaak hizo lo creó [en] k’uh…j, él es dios.\";s:6:\"img_hp\";s:9:\"r18_5.png\";s:10:\"texto_maya\";s:50:\"<i>iuch’ahb[a]w Chaak k’uh…[a]j, k’uh</i>.\";s:12:\"coordenada_x\";s:3:\"367\";s:12:\"coordenada_y\";s:2:\"70\";}i:5;a:5:{s:13:\"texto_espanol\";s:40:\"Chaak lo creó en el cielo, él es dios.\";s:6:\"img_hp\";s:9:\"r18_6.png\";s:10:\"texto_maya\";s:46:\"<i>uch’ahb[a]w Chaak ti ka’an, k’uh</i>.\";s:12:\"coordenada_x\";s:3:\"453\";s:12:\"coordenada_y\";s:2:\"70\";}i:6;a:5:{s:13:\"texto_espanol\";s:67:\"Chaak está sobre la diosa, él es dios’. Ix k’uh, ‘la diosa.\";s:6:\"img_hp\";s:9:\"r18_7.png\";s:10:\"texto_maya\";s:43:\"<i>ayan[a]w Chaak ti ix k’uh, k’uh</i>.\";s:12:\"coordenada_x\";s:3:\"530\";s:12:\"coordenada_y\";s:2:\"70\";}i:7;a:5:{s:13:\"texto_espanol\";s:61:\"Chaak lo creó [en] el árbol de pito, pie de la primer casa.\";s:6:\"img_hp\";s:9:\"r18_8.png\";s:10:\"texto_maya\";s:55:\"<i>uch’ahb[a]w Chaak tz’ite’, yaax na[h] ook</i>.\";s:12:\"coordenada_x\";s:3:\"637\";s:12:\"coordenada_y\";s:2:\"70\";}i:8;a:5:{s:13:\"texto_espanol\";s:50:\"Chaaki’ lo creó en el jiote, señor trabajador.\";s:6:\"img_hp\";s:9:\"r18_9.png\";s:10:\"texto_maya\";s:56:\"<i>iuch’ahb[aw] Chaaki’ ti chikaj, men[e]l ajaw</i>.\";s:12:\"coordenada_x\";s:3:\"722\";s:12:\"coordenada_y\";s:2:\"70\";}}'),
