	(6167,642,'repeatable_fields_2','a:6:{i:0;a:5:{s:13:\"texto_espanol\";s:31:\"‘…, es el anuncio de Chaak.\";s:6:\"img_hp\";s:9:\"r20_1.png\";s:10:\"texto_maya\";s:29:\"<i>…, [u]mu’uk Chaak</i>.\";s:12:\"coordenada_x\";s:2:\"91\";s:12:\"coordenada_y\";s:2:\"14\";}i:1;a:5:{s:13:\"texto_espanol\";s:52:\"Chaak está en los carrizos, en las cañas de maíz.\";s:6:\"img_hp\";s:9:\"r20_2.png\";s:10:\"texto_maya\";s:46:\"<i>ayan ta jal[al](?) ta jal[al](?) Chaak</i>.\";s:12:\"coordenada_x\";s:3:\"199\";s:12:\"coordenada_y\";s:2:\"14\";}i:2;a:5:{s:13:\"texto_espanol\";s:35:\"Chaak está en … de mucha comida.\";s:6:\"img_hp\";s:9:\"r20_3.png\";s:10:\"texto_maya\";s:39:\"<i>ayan[a]w … ox wi’[il] Chaak</i>.\";s:12:\"coordenada_x\";s:3:\"301\";s:12:\"coordenada_y\";s:2:\"14\";}i:3;a:5:{s:13:\"texto_espanol\";s:46:\"el jugador de pelota Chaak está en la cancha.\";s:6:\"img_hp\";s:9:\"r20_4.png\";s:10:\"texto_maya\";s:33:\"<i>[ayan] alaw pitziil Chaak</i>.\";s:12:\"coordenada_x\";s:3:\"428\";s:12:\"coordenada_y\";s:2:\"13\";}i:4;a:5:{s:13:\"texto_espanol\";s:48:\"Chaak está en …, [el augurio es] alimento(?).\";s:6:\"img_hp\";s:9:\"r20_5.png\";s:10:\"texto_maya\";s:33:\"<i>alaw … o’och(?) Chaak</i>.\";s:12:\"coordenada_x\";s:3:\"553\";s:12:\"coordenada_y\";s:2:\"14\";}i:5;a:5:{s:13:\"texto_espanol\";s:75:\"Es probable que diga: [ayan yotoch] Chaak …, ‘Chaak [está en su casa].\";s:6:\"img_hp\";s:9:\"r20_6.png\";s:10:\"texto_maya\";s:19:\"<i>Detariorado</i>.\";s:12:\"coordenada_x\";s:3:\"678\";s:12:\"coordenada_y\";s:2:\"14\";}}'),
